function cc=getColourCircle( center, ncolours, amplitude)% calculate number of colours lying on a circle in CIE space%	center is cie value of middle of circle 1=x, 2=y, 3 = luminance%%step=360/ncolours;cc=zeros(ncolours,3);i=0;for a=0:step:359.9	i=i+1;	cc(i,1) = center(1) + amplitude * sin(a/360*2*pi); % x	cc(i,2) = center(2) + amplitude * cos(a/360*2*pi); % y	cc(i,3) = center(3);end