function waitForAction(release)% waits for a mouse button or key press% set release=1 to also wait for release of mouse and keys% 19-02-04 fwc removed window argument to make OS X compatibleif ~exist('release', 'var') | isempty(release)	release=0;endwhile 1	[keyIsDown,secs,keyCode] = KbCheck;	[x,y,button] = GetMouse;	if keyIsDown==1 | button==1		break;	endendif release == 1	waitForRelease;end