function forb = NewfORBOpen(forb)% open portid for fORB boxswitch forb.portid	case 0, % do not use fORB box		forb.port=0;		forb.mode=0; 	case 1,		forb.port=SERIAL('Open','.AIn', '.AOut',9600); % modem portid		forb.mode=1;	case 2,		forb.port=SERIAL('Open','.BIn', '.BOut',9600); % printer portid		forb.mode=1;	otherwise,		if forb.print==1			fprintf('\nfORBOpen error: unsupported portid request (%d).\n', forb.portid);		end		forb.portid=0;		forb.port=0;		forb.mode=-1;		forb.error=1;end