function forb=NewfORBInitScanner(forb, simulate, TR, slices, slicetime)%% Set scanner parameters for (simulated) triggering% USAGE: forb=NewfORBInitScanner(forb, simulate, TR, slices)%% 16-02-02	fwc		added slicetime parameter%					added volumegaptime, nextvolumetime field used in newer countdown procedure%if nargin<=4 | isempty(slicetime)	slicetime=0.1;endforb.scanner.simulate=simulate;forb.scanner.TR=TR;forb.scanner.nextvolumetime=TR/2;forb.scanner.slices=slices;forb.scanner.sliceinterval=slicetime;forb.scanner.volumegaptime=4*forb.scanner.sliceinterval;forb.scanner.volumeinterval=TR-slices*forb.scanner.sliceinterval;forb.scanner.start=-999999;forb.scanner.pulseduration=1/75*3;