function DrawText( text, window, h, v, colour )% draws text on screen% USAGE: DrawText( text [, window, colour, h, v] )% default window is front window% default colour is white% default position is centeredif ~exist('window', 'var') | isempty( window)	window=SCREEN('GetFrontWindow');endif ~exist('colour', 'var') | isempty( colour)	colour=whiteindex(window);end[x,y] = WindowCenter(window);width=SCREEN(window,'TextWidth',text);if ~exist('h', 'var') | isempty(h)	h=x-width/2;endif ~exist('v', 'var') | isempty(v)	v=y;endSCREEN(window,'DrawText',text,h,v,colour);