function [newfontsize, oldfontsize]=setfont( window, size, name, style )% USAGE: setfont( window, size [, name, style] )% default window is frontwindow% size is a percentage of screen widthif ~exist('window', 'var') | isempty( window)	window=SCREEN('GetFrontWindow');endif exist('name', 'var') & ~isempty(name)	Screen(window,'TextFont', name);endif exist('style', 'var') & ~isempty(style)	if isempty(str2num( style )) % style is string, convert string to number		switch style		case 'normal'			style=0;		case 'bold'			style=1;		case 'italic'			style=2;		case 'underline'			style=4;		case 'outline'			style=8;		case 'condense'			style=32;		case 'extend'			style=64;		otherwise			style=0;		end	end	Screen(window,'TextStyle',style);end% set size as a percentage of screen width[w,h]=windowsize(window);fontsize=round(w/100*size);oldfontsize=Screen(window,'TextSize',fontsize);newfontsize=Screen(window,'TextSize');